mapper.select:
SELECT /*temp.`identify` te_identify,*/te.`id` te_id,te.`full_name` te_full_name,te.`UCCcode` te_UCCcode,te.`website` te_website,te.`main_product` te_main_product,te.`level` te_level,ttg.`name` ttg_name
FROM `table_enterprise` AS te
LEFT JOIN `table_trade_group` AS ttg ON ttg.`id` = te.`trade_group_id`
/*LEFT JOIN (SELECT @rownum:=@rownum+1 AS identify, `id` FROM (SELECT @rownum:=0) a,`table_enterprise`) AS temp ON te.`id` = temp.`id`*/



      1=1


          AND `category_id` = #{enterpriseCondition.categoryId}

        /*按分组查询*/

          AND

            `trade_group_id` = #{group}


        /*按经营范围查询*/
        AND `operate_range` LIKE '%' #{enterpriseCondition.operateRange} '%'



            OR `full_name` LIKE '%' #{enterpriseCondition.fullName} '%'
            OR `UCCcode` LIKE '%' #{enterpriseCondition.uccCode} '%'


            AND `full_name` LIKE '%' #{enterpriseCondition.fullName} '%'
            AND `UCCcode` LIKE '%' #{enterpriseCondition.uccCode} '%'


        AND `exception_info` IS NULL AND `danger_info` IS NULL

        ORDER BY `enter_date` DESC

          ,`${enterpriseCondition.sort}`

            ${enterpriseCondition.sorted}







    ORDER BY `enter_date` DESC




  LIMIT #{offset},#{pageSize}
